import Board from './Board/Board.jsx';
import React, { useEffect, useState } from 'react';
import './App.css';


const Speed = {
  BEGINNER: 200,
  INTERMEDIATE: 150,
  PRO: 90
};
const handleChange = (e) => {
  let value = e.target.value;
  if (value == "BEGINNER") {
    setSpeed(Speed.BEGINNER);

  }
  else if (value == "INTERMEDIATE") {
    setSpeed(Speed.INTERMEDIATE)
  }
  else {
    setSpeed(Speed.PRO)
  }

};
const App = () => (
  <div className="App">
    <div>
        <h2 style={{ float: "top" }}>Choose level :</h2>
        <div style={{ float: "center"}} >
          <select  className="level" 
            onChange={(e) => handleChange(e)} disabled={false}>
            <option className="value" value="BEGINNER">Beginner</option>
            <option className="value" value="INTERMEDIATE">Intermediate</option>
            <option className="value" value="PRO">Pro</option>
          </select>
        </div>
      </div>
    <Board></Board>
  </div>
);

export default App;
